import React,{Component} from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import MyNav from './MyNav.js';
import MyFooter from './footer.js';
import SearchApp from './SearchApp';
import LoginPage from './loginpage';
import SignupPage from './signuppage'




class Main extends React.Component {


  constructor(props){

    super(props);

    this.state = {


        login:'home',

        isLogin:false,

    };

  }

  onLogoutClick =({page})=>{

    if(page==='signout'){

      this.setState({isLogin:false});
    }

    else if(page==='home'){

        this.setState({isLogin:true});
    }

     this.setState({login:page});

  }

  render(){


                return(

              	<div>

                {

                 this.state.login==='home' ?



                <div>

             	  <MyNav onLogoutClick={this.onLogoutClick}/>


                <SearchApp/>


                </div>

               : (   

                    this.state.login==='signin' ?

                    <LoginPage onLogoutClick={this.onLogoutClick}/>

                    :

                    <SignupPage onLogoutClick={this.onLogoutClick}/>


               )
            }

             <MyFooter/>

            </div>


)}

}


export default Main;
